mapper.select:
 	select * from interview i left join ir_info ir
	on i.imember_info_no = ir.member_info_no
 	where i.interview_no = #{interviewNo}

mapper.update:
update interview set 
title = #{title},
content = #{content},
reg_date = default,
imember_info_no = #{iMemberInfoNo}
where interview_no = #{interviewNo}
