mapper.update:
update ir_info set
name = #{name},
email = #{email},
phone = #{phone},
category = #{category}
where member_info_no = #{memberInfoNo}

mapper.select:
select * from ir_info
where member_ir_haed_no = #{memberNo}
and role = 'I'

mapper.insert:

	select co_code from ir_info where member_info_no = #{memberInfoNo}

insert into interview_time values(
	seq_itime_no.nextval,
	#{memberInfoNo},
	#{coCode},
	#{startTime},
	#{endTime},
	#{jobPostingName}
)

mapper.update:
update interview_time set
start_time = #{startTime},
end_time = #{endTime},
job_posting_name = #{jobPostingName}
where member_info_no = #{memberInfoNo}

update ir_info set
name = #{name},
email = #{email},
phone = #{phone}
where member_info_no = #{memberInfoNo}

mapper.select:
select count(*) from ir_info
where name = #{name}
and email = #{email}
and phone = #{phone}

