mapper.update:
update member set co_code = #{coCode}, name = #{name}, gender = #{gender}, email = #{email}, phone = #{phone}, address = #{address}, kind = #{kind}
, interviewer_no = #{interviewerNo}
where id = #{id}

mapper.select:
select name, phone, co_code from ir_info
where name = #{name}
  and ssn like #{birthday}||'%'
  and phone = #{phone}
  and co_code = #{coCode}
  and email = #{email}

  and interviewer_no = #{interviewerNo}

select ir.member_info_no as "groupId" , co_name || ' ' || t.job_posting_name as "title" , cast(t.start_time AS DATE) as "start" , cast(t.end_time AS DATE) as "end" from interview_time t, ir_info ir, member_company co where ir.member_info_no = t.member_info_no and ir.co_code=co.co_code
and co.co_code = #{coCode}
and member_no = #{memberNo}
